import React, { useEffect, useState } from "react";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [checked, setChecked] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  const handleChange = () => setChecked((prev) => !prev);

  const productLoad = async () => {
    try {
      setLoading(true);
      await fetch(`data/${checked ? "sale_" : ""}products.json`)
        .then((res) => res.json())
        .then((data) => {
          console.log("üî•Îú®ÎÅàÌïú Îç∞Ïù¥ÌÑ∞Î•º ÎÑ§Ìä∏ÏõåÌÅ¨ÏóêÏÑú Î∞õÏïÑÏò¥");
          setProducts(data);
        });
      setLoading(false);
      return () => {
        console.log("üßπ Íπ®ÎÅóÌïòÍ≤å Ï≤≠ÏÜåÌïòÎäî ÏùºÎì§ÏùÑ Ìï©ÎãàÎã§.");
      };
    } catch (error) {
      setError(error);
    }
  };

  useEffect(() => {
    productLoad();
  }, [checked]);

  return error !== null ? (
    <div>ÏóêÎü¨Î∞úÏÉù: {error}</div>
  ) : loading ? (
    <div style={{ margin: "15px 0" }}>Loading...</div>
  ) : (
    <>
      <input
        id="checkbox"
        type="checkbox"
        value={checked}
        onChange={handleChange}
      />
      <label htmlFor="checkbox">Show Only üî• Sale</label>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <article>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
            </article>
          </li>
        ))}
      </ul>
    </>
  );
}
