import React, { useReducer } from 'react';
import personReducer from './reducer/person-reducer';

export default function AppMentorsButton() {
  const [person, dispatch] = useReducer(personReducer, initialPerson);

  const handleUpdate = () => {
    const prev = prompt(`ÎàÑÍµ¨Ïùò Ïù¥Î¶ÑÏùÑ Î∞îÍæ∏Í≥† Ïã∂ÏùÄÍ∞ÄÏöî?`);
    const current = prompt(`Ïù¥Î¶ÑÏùÑ Î¨¥ÏóáÏúºÎ°ú Î∞îÍæ∏Í≥† Ïã∂ÏùÄÍ∞ÄÏöî?`);
    dispatch({ type: 'updated', prev, current });
  };

  const handleAdd = () => {
    const name = prompt(`Î©òÌÜ†Ïùò Ïù¥Î¶ÑÏùÄ?`);
    const title = prompt(`Î©òÌÜ†Ïùò ÏßÅÌï®ÏùÄ?`);
    dispatch({ type: 'added', name, title });
  };

  const handleDelete = () => {
    const name = prompt(`ÎàÑÍµ¨Î•º ÏÇ≠Ï†úÌïòÍ≥† Ïã∂ÏùÄÍ∞ÄÏöî?`);
    dispatch({ type: 'deleted', name });
  };

  return (
    <div>
      <h1>
        {person.name}Îäî {person.title}
      </h1>
      <p>{person.name}Ïùò Î©òÌÜ†Îäî:</p>
      <ul>
        {person.mentors.map((mentor, index) => (
          <li key={index}>
            {mentor.name} ({mentor.title})
          </li>
        ))}
      </ul>
      <button onClick={handleUpdate}>Î©òÌÜ†Ïùò Ïù¥Î¶ÑÏùÑ Î∞îÍæ∏Í∏∞</button>
      <button onClick={handleAdd}>Î©òÌÜ† Ï∂îÍ∞ÄÌïòÍ∏∞</button>
      <button onClick={handleDelete}>Î©òÌÜ† ÏÇ≠Ï†úÌïòÍ∏∞</button>
    </div>
  );
}

function Button({ text, onClick }) {
  console.log('Button', text, 're-rendering üòú');
  return (
    <button
      onClick={onClick}
      style={{
        backgroundColor: 'black',
        color: 'white',
        borderRadius: '20px',
        margin: '0.4rem',
      }}
    >
      {text}
    </button>
  );
}

const initialPerson = {
  name: 'ÏóòÎ¶¨',
  title: 'Í∞úÎ∞úÏûê',
  mentors: [
    {
      name: 'Î∞•',
      title: 'ÏãúÎãàÏñ¥Í∞úÎ∞úÏûê',
    },
    {
      name: 'Ï†úÏûÑÏä§',
      title: 'ÏãúÎãàÏñ¥Í∞úÎ∞úÏûê',
    },
  ],
};
