import React, { useEffect, useState } from "react";

export default function Products({ load }) {
  const [products, setProducts] = useState([]);
  const [checked, setChecked] = useState(false);
  const [loading, setLoading] = useState(true);
  const handleChange = () => setChecked((prev) => !prev);

  const productLoad = async () => {
    setLoading(false);
    isLoading(loading);
    await fetch(`data/${checked ? "sale_" : ""}products.json`)
      .then((res) => res.json())
      .then((data) => {
        console.log("üî•Îú®ÎÅàÌïú Îç∞Ïù¥ÌÑ∞Î•º ÎÑ§Ìä∏ÏõåÌÅ¨ÏóêÏÑú Î∞õÏïÑÏò¥");
        setProducts(data);
      });
    setLoading(true);
    isLoading(loading);

    return () => {
      setLoading(true);
      console.log("üßπ Íπ®ÎÅóÌïòÍ≤å Ï≤≠ÏÜåÌïòÎäî ÏùºÎì§ÏùÑ Ìï©ÎãàÎã§.");
    };
  };

  useEffect(productLoad(), [checked]);

  return (
    <>
      <input
        id="checkbox"
        type="checkbox"
        value={checked}
        onChange={handleChange}
      />
      <label htmlFor="checkbox">Show Only üî• Sale</label>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <article>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
            </article>
          </li>
        ))}
      </ul>
    </>
  );
}
