import React, { useEffect, useState } from 'react';

export default function Products() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState();
  const [products, setProducts] = useState([]);
  const [checked, setChecked] = useState(false);
  const handleChange = () => setChecked((prev) => !prev);

  useEffect(() => {
    setLoading(true);
    setError(undefined);
    fetch(`data/${checked ? 'sale_' : ''}products.json`)
      .then((res) => res.json())
      .then((data) => {
        console.log('üî•Îú®ÎÅàÌïú Îç∞Ïù¥ÌÑ∞Î•º ÎÑ§Ìä∏ÏõåÌÅ¨ÏóêÏÑú Î∞õÏïÑÏò¥');
        setProducts(data);
      })
      .catch((e) => setError('ÏóêÎü¨Í∞Ä Î∞úÏÉùÌñàÏùå!'))
      .finally(() => setLoading(false));
    return () => {
      console.log('üßπ Íπ®ÎÅóÌïòÍ≤å Ï≤≠ÏÜåÌïòÎäî ÏùºÎì§ÏùÑ Ìï©ÎãàÎã§.');
    };
  }, [checked]);

  if (loading) return <p>Loading...</p>;

  if (error) return <p>{error}</p>;

  return (
    <>
      <input
        id='checkbox'
        type='checkbox'
        value={checked}
        onChange={handleChange}
      />
      <label htmlFor='checkbox'>Show Only üî• Sale</label>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <article>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
            </article>
          </li>
        ))}
      </ul>
    </>
  );
}
